// Custom render function with all providers
interface CustomRenderOptions extends Omit<RenderOptions, 'wrapper'> {
  initialEntries?: string[];
  queryClient?: QueryClient;
  deviceInfo?: typeof mockDeviceInfo;
  languageContext?: typeof mockLanguageContext;
  withErrorBoundary?: boolean;
export function renderWithProviders(
  ui: ReactElement,
  options: CustomRenderOptions = {}
): RenderResult {
  const {
    initialEntries = ['/'],
    queryClient = new QueryClient({
      defaultOptions: {
        queries: {
          retry: false,
          gcTime: 0,
        },
        mutations: {
          retry: false,
        },
      },
    }),
    deviceInfo = mockDeviceInfo,
    languageContext = mockLanguageContext,
    withErrorBoundary = true,
    ...renderOptions
  } = options;

  // Mock device detection hook
  jest.mock('@/contexts/DeviceContext', () => ({
    useDeviceDetection: () => deviceInfo,
    DeviceProvider: ({ children }: { children: ReactNode }) => <>{children}</>,
  }));

  // Mock language context hook
  jest.mock('@/contexts/LanguageContext', () => ({
    useLanguage: () => languageContext,
    LanguageProvider: ({ children }: { children: ReactNode }) => <>{children}</>,
  }));

  function Wrapper({ children }: { children: ReactNode }) {
    const content = (
      <QueryClientProvider client={queryClient}>
        <BrowserRouter>
          <DeviceProvider>
            <LanguageProvider>
              <TooltipProvider>
                {children}
              </TooltipProvider>
            </LanguageProvider>
          </DeviceProvider>
        </BrowserRouter>
      </QueryClientProvider>
    );

    return withErrorBoundary ? (
      <ErrorBoundary>{content}</ErrorBoundary>
    ) : content;

  return render(ui, { wrapper: Wrapper, ...renderOptions });

