import React, {  useState, useEffect , useCallback } from 'react';
import { Link } from 'react-router-dom';
import { Menu, X } from 'lucide-react';

import LanguageSelector from '@/components/LanguageSelector';
import SafeImage from '@/components/SafeImage';
import SkipLink from '@/components/SkipLink';
import ThemeToggle from '@/components/ThemeToggle';
import { Button } from '@/components/ui/button';
import { cn } from '@/lib/utils';
import { useLanguage } from '@/contexts/LanguageContext';

const Navbar: React.FC = () => {
  const { t } = useLanguage();
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [scrolled, setScrolled] = useState(false);
  const [activeSubmenu, setActiveSubmenu] = useState<string | null>(null);
  const [hoverTimeout, setHoverTimeout] = useState<NodeJS.Timeout | null>(null);

  const navLinks = [
    { name: t.nav.home, path: "/" },
    {
      name: t.nav.expertise,
      path: "/expertise",
      submenu: [
        { name: t.nav.expertiseSubmenu.cervicalDisc, path: "/expertise/cervical-disc-replacement" },
        { name: t.nav.expertiseSubmenu.lumbarDisc, path: "/expertise/lumbar-disc-replacement" },
        { name: t.nav.expertiseSubmenu.imageGuided, path: "/expertise/image-guided-surgery" },
        { name: t.nav.expertiseSubmenu.roboticSpine, path: "/expertise/robotic-spine-surgery" },
      ]
    },
    { name: t.nav.appointments, path: "/appointments" },
    {
      name: t.patientResources.title,
      path: "/patient-resources",
      submenu: [
        { name: t.patientResources.submenu.comprehensiveSolution, path: "/patient-resources/individual-spine-health-programme" },
        { name: t.patientResources.submenu.conditionInfo, path: "/patient-resources/condition-information" },
        { name: t.patientResources.submenu.exerciseLibrary, path: "/patient-resources/exercise-library" },
        { name: t.nav.faq, path: "/faq" },
      ]
    },
    {
      name: t.nav.gpResources,
      path: "/gp-resources",
      submenu: [
        { name: t.nav.gpResourcesSubmenu.referralProtocols, path: "/gp-resources/referral-protocols" },
        { name: t.nav.gpResourcesSubmenu.diagnostics, path: "/gp-resources/diagnostics" },
        { name: t.nav.gpResourcesSubmenu.careCoordination, path: "/gp-resources/care-coordination" },
        { name: t.nav.gpResourcesSubmenu.emergencies, path: "/gp-resources/emergencies" },
      ]
    },
    { name: t.nav.medicolegal, path: "/medicolegal" },
    {
      name: t.nav.locations,
      path: "/locations",
      submenu: [
        { name: t.nav.locationsSubmenu.mornington, path: "/locations/mornington" },
        { name: t.nav.locationsSubmenu.frankston, path: "/locations/frankston" },
        { name: t.nav.locationsSubmenu.langwarrin || "Langwarrin", path: "/locations/langwarrin" },
        { name: t.nav.locationsSubmenu.mooneePonds, path: "/locations/moonee-ponds" },
        { name: t.nav.locationsSubmenu.sunbury, path: "/locations/sunbury" },
        { name: t.nav.locationsSubmenu.werribee, path: "/locations/werribee" },
        { name: t.nav.locationsSubmenu.bundoora, path: "/locations/bundoora" },
        { name: t.nav.locationsSubmenu.dandenong, path: "/locations/dandenong" },
        { name: t.nav.locationsSubmenu.heidelberg, path: "/locations/heidelberg" },
        { name: t.nav.locationsSubmenu.wantirna, path: "/locations/wantirna" },
        { name: t.nav.locationsSubmenu.surreyHills, path: "/locations/surrey-hills" },
      ]
    },
    { name: t.nav.contact, path: "/contact" }
  ];

  useEffect(() => {
    const handleScroll = () => {
      const isScrolled = window.scrollY > 20;
      if (isScrolled !== scrolled) {
        setScrolled(isScrolled);
      }
    };
    window.addEventListener("scroll", handleScroll);

    // Close submenu when clicking outside
    const handleClickOutside = (e: MouseEvent) => {
      if (activeSubmenu && !(e.target as Element).closest('.nav-item-with-submenu')) {
        setActiveSubmenu(null);
      }
    };
    document.addEventListener('click', handleClickOutside);

    return () => {
      window.removeEventListener("scroll", handleScroll);
      document.removeEventListener('click', handleClickOutside);
      if (hoverTimeout) {
        clearTimeout(hoverTimeout);
      }
    };
  }, [scrolled, activeSubmenu, hoverTimeout]);

  // Function to handle submenu hover with delay
  const handleSubmenuEnter = (name: string) => {
    if (hoverTimeout) {
      clearTimeout(hoverTimeout);
      setHoverTimeout(null);
    }
    setActiveSubmenu(name);
  };

  const handleSubmenuLeave = () => {
    const timeout = setTimeout(() => {
      setActiveSubmenu(null);
    }, 150); // 150ms delay before closing
    setHoverTimeout(timeout);
  };



  // Function to handle keyboard navigation
  const handleKeyDown = (e: React.KeyboardEvent, name: string, hasSubmenu: boolean) => {
    if (hasSubmenu && e.key === 'ArrowDown') {
      // Arrow down always opens the submenu
      e.preventDefault();
      setActiveSubmenu(name);
    } else if (hasSubmenu && (e.key === 'Enter' || e.key === ' ')) {
      // Enter or Space navigates to the page if submenu is open, otherwise opens submenu
      if (activeSubmenu === name) {
        // Submenu is open, let the navigation happen
        return;
      } else {
        // Submenu is closed, open it
        e.preventDefault();
        setActiveSubmenu(name);
      }
    }
  };

  return (
    <header className={cn("fixed top-0 left-0 right-0 z-50 transition-all duration-300 border-0",
      scrolled ? "bg-white/95 dark:bg-card/95 backdrop-blur-lg py-3 shadow-md" : "bg-white/90 dark:bg-card/90 backdrop-blur-sm py-5")}>
      <SkipLink />
      <nav className="container flex justify-between items-center" aria-label="Main navigation">
        <div className="flex items-center gap-4">
          <Link to="/" className="flex items-center">
            <SafeImage
              src="/logo/logo.png"
              alt="miNEURO Logo"
              className="h-10 w-auto"
              fallbackSrc="/logo/logo-white.png"
            />
          </Link>
          <LanguageSelector />
        </div>

        {/* Desktop Navigation */}
        <ul className="hidden lg:flex items-center space-x-6" role="menubar">
          {navLinks?.map(link => (
            <li key={link.name} className={`relative nav-item-with-submenu ${activeSubmenu === link.name ? 'active' : ''}`} role="none">
              {link.submenu ? (
                <>
                  <Link
                    to={link.path}
                    className="font-medium transition-colors hover:text-primary flex items-center text-base whitespace-nowrap"
                    onMouseEnter={() => handleSubmenuEnter(link.name)}
                    onMouseLeave={handleSubmenuLeave}
                    onKeyDown={useCallback((e: any) => handleKeyDown(e, link.name, true), [])}
                    aria-expanded={activeSubmenu === link.name}
                    aria-haspopup="true"
                    role="menuitem"
                    tabIndex={0}
                  >
                    {link.name}
                    <span className="ml-1 inline-block text-sm">▾</span>
                  </Link>

                  {/* Dropdown for items with submenu */}
                  <div
                    className={`absolute left-0 mt-2 w-56 rounded-md shadow-lg bg-card ring-1 ring-border transition-opacity duration-200 z-50 ${
                      activeSubmenu === link.name ? 'opacity-100 visible' : 'opacity-0 invisible'
                    }`}
                    role="menu"
                    aria-orientation="vertical"
                    aria-labelledby={`${link.name.toLowerCase()}-menu`}
                    onMouseEnter={() => handleSubmenuEnter(link.name)}
                    onMouseLeave={handleSubmenuLeave}
                  >
                    <div className="py-1">
                      {link.submenu?.map((subItem: any) => (
                        <Link
                            key={subItem.name}
                          to={subItem.path}
                          className="block px-4 py-2 text-sm text-foreground hover:bg-muted hover:text-primary"
                          role="menuitem"
                          tabIndex={activeSubmenu === link.name ? 0 : -1}
                          onClick={() => setActiveSubmenu(null)}
                        >
                          {subItem.name}
                        </Link>
                      ))}
                    </div>
                  </div>
                </>
              ) : (
                <Link
                  to={link.path}
                  className="font-medium transition-colors hover:text-primary text-base whitespace-nowrap"
                  role="menuitem"
                  tabIndex={0}
                >
                  {link.name}
                </Link>
              )}
            </li>
          ))}
        </ul>

        <div className="hidden lg:flex items-center space-x-2">
          <ThemeToggle />
          <Button asChild className="btn-primary">
            <Link to="/appointments">{t.nav.bookNow}</Link>
          </Button>
        </div>

        {/* Mobile Navigation */}
        <div className="lg:hidden flex items-center space-x-2">
          <ThemeToggle />
          <Button
            variant="ghost"
            size="icon"
            onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
            className="rounded-full"
            aria-label={mobileMenuOpen ? "Close menu" : "Open menu"}
            aria-expanded={mobileMenuOpen}
          >
            {mobileMenuOpen ? <X className="h-6 w-6" /> : <Menu className="h-6 w-6" />}
          </Button>
        </div>
      </nav>

      {/* Mobile Menu */}
      <div
        className={cn("fixed inset-0 z-40 bg-background/80 backdrop-blur-sm lg:hidden transition-opacity duration-300",
        mobileMenuOpen ? "opacity-100" : "opacity-0 pointer-events-none")}
        role="dialog"
        aria-modal="true"
        aria-label="Mobile navigation menu"
        id="mobile-menu"
      >
        <div
          className={cn("fixed inset-y-0 right-0 w-3/4 max-w-sm bg-card shadow-xl p-6 transition-transform duration-300 ease-in-out",
          mobileMenuOpen ? "translate-x-0" : "translate-x-full")}
        >
          <div className="flex flex-col h-full justify-between">
            <div>
              <div className="flex justify-between items-center mb-8">
                <div className="flex items-center gap-4">
                  <Link to="/" onClick={() => setMobileMenuOpen(false)} className="flex items-center">
                    <SafeImage
                      src="/logo/logo.png"
                      alt="miNEURO Logo"
                      className="h-10 w-auto"
                      fallbackSrc="/logo/logo-white.png"
                    />
                  </Link>
                  <LanguageSelector />
                </div>
                <Button
                  variant="ghost"
                  size="icon"
                  onClick={() => setMobileMenuOpen(false)}
                  className="rounded-full ml-2"
                  aria-label="Close menu"
                >
                  <X className="h-6 w-6" aria-hidden="true" />
                </Button>
              </div>
              <nav aria-label="Mobile navigation">
                <ul className="space-y-6" role="menu">
                  {navLinks?.map(link => (
                    <li key={link.name} role="none">
                      <Link
                        to={link.path}
                        className="text-lg font-medium transition-colors hover:text-primary block"
                        onClick={useCallback((e) => {
                          // Check if the click was on the dropdown arrow
                          const target = e.currentTarget as HTMLElement;
                          const rect = target.getBoundingClientRect();
                          const isClickNearRightEdge = (e.clientX > rect.right - 40);

                          if (link.submenu && isClickNearRightEdge) {
                            // If clicking on the dropdown arrow, toggle submenu
                            e.preventDefault();
                            setActiveSubmenu(activeSubmenu === link.name ? null : link.name);
                          } else {
                            // Otherwise navigate to the page
                            setMobileMenuOpen(false);
                          }
                        }, [])}
                        aria-expanded={link.submenu ? activeSubmenu === link.name : undefined}
                        aria-haspopup={link.submenu ? 'true' : undefined}
                        role="menuitem"
                      >
                        {link.name}
                        {link.submenu && <span className="ml-1 inline-block float-right">▾</span>}
                      </Link>

                      {/* Mobile submenu */}
                      {link.submenu && (
                        <div
                          className={`pl-6 mt-2 space-y-2 ${activeSubmenu === link.name ? 'block' : 'hidden'}`}
                          role="menu"
                          aria-label={`${link.name} submenu`}
                        >
                          {link.submenu?.map((subItem) => (
                            <Link
                              key={subItem.name}
                              to={subItem.path}
                              className="block text-sm text-muted-foreground hover:text-primary"
                              onClick={() => setMobileMenuOpen(false)}
                              role="menuitem"
                            >
                              {subItem.name}
                            </Link>
                          ))}
                        </div>
                      )}
                    </li>
                  ))}
                </ul>
              </nav>
            </div>

            <Button asChild className="w-full btn-primary mt-6">
              <Link to="/appointments" onClick={() => setMobileMenuOpen(false)}>
                {t.nav.bookNow}
              </Link>
            </Button>
          </div>
        </div>
      </div>
    </header>
  );
};

Navbar.displayName = 'Navbar';

export default Navbar;

import React from 'react';

/**
 * Performance metrics interface
 */
export interface PerformanceMetrics {
  name: string;
  startTime: number;
  endTime?: number;
  duration?: number;
  metadata?: Record<string, unknown>;
}

/**
 * Performance monitoring configuration
 */
interface PerformanceConfig {
  enableResourceTiming: boolean;
  enableNavigationTiming: boolean;
  enableUserTiming: boolean;
  enableLongTaskTiming: boolean;
  enableLayoutShiftTiming: boolean;
  enableLargestContentfulPaint: boolean;
  enableFirstInputDelay: boolean;
  maxMetrics: number;
}

/**
 * Performance monitoring singleton class
 */
export class PerformanceMonitor {
  private static instance: PerformanceMonitor;
  private metrics: Map<string, PerformanceMetrics> = new Map();
  private observers: PerformanceObserver[] = [];
  private config: PerformanceConfig;
  private initialized = false;
  private initializing = false;

  private constructor() {
    this.config = {
      enableResourceTiming: true,
      enableNavigationTiming: true,
      enableUserTiming: true,
      enableLongTaskTiming: true,
      enableLayoutShiftTiming: true,
      enableLargestContentfulPaint: true,
      enableFirstInputDelay: true,
      maxMetrics: 1000
    };
  }

  static getInstance(): PerformanceMonitor {
    if (!PerformanceMonitor.instance) {
      PerformanceMonitor.instance = new PerformanceMonitor();
    }
    return PerformanceMonitor.instance;
  }

  /**
   * Initialize performance monitoring
   */
  initialize(): void {
    if (this.initialized || this.initializing) {
      return;
    }

    this.initializing = true;

    try {
      if (typeof window === 'undefined' || !('performance' in window)) {
        this.initializing = false;
        return;
      }

      // Initialize observers
      this.initializeObservers();

      this.initialized = true;
      this.initializing = false;
    } catch (error) {
      // Reset flags on failure and cleanup any partial observers
      this.initializing = false;
      this.initialized = false;
      this.cleanupObservers();
    }
  }

  /**
   * Initialize performance observers
   */
  private initializeObservers(): void {
    if (!('PerformanceObserver' in window)) {
      return;
    }

    try {
      // Navigation timing observer
      if (this.config.enableNavigationTiming) {
        const navObserver = new PerformanceObserver((list) => {
          list.getEntries().forEach((entry) => {
            if (entry.entryType === 'navigation') {
              this.logNavigationMetrics(entry as PerformanceNavigationTiming);
            }
          });
        });
        navObserver.observe({ entryTypes: ['navigation'] });
        this.observers.push(navObserver);
      }

      // Resource timing observer
      if (this.config.enableResourceTiming) {
        const resourceObserver = new PerformanceObserver((list) => {
          list.getEntries().forEach((entry) => {
            if (entry.entryType === 'resource') {
              this.logResourceMetrics(entry as PerformanceResourceTiming);
            }
          });
        });
        resourceObserver.observe({ entryTypes: ['resource'] });
        this.observers.push(resourceObserver);
      }
    } catch (error) {
      // Silently handle observer initialization errors
    }
  }

  /**
   * Start measuring a performance metric
   */
  startMeasure(name: string, metadata?: Record<string, unknown>): void {
    const startTime = performance.now();
    this.metrics.set(name, {
      name,
      startTime,
      metadata
    });
  }

  /**
   * End measuring a performance metric
   */
  endMeasure(name: string): PerformanceMetrics | null {
    const metric = this.metrics.get(name);
    if (!metric) {
      return null;
    }
    const endTime = performance.now();
    const duration = endTime - metric.startTime;

    const completedMetric: PerformanceMetrics = {
      ...metric,
      endTime,
      duration
    };

    this.metrics.set(name, completedMetric);
    this.logMetric(name, metric.startTime, endTime, metric.metadata);

    return completedMetric;
  }

  /**
   * Log a performance metric
   */
  private logMetric(
    _name: string,
    _startTime: number,
    _endTime: number,
    _metadata?: Record<string, unknown>
  ): void {
    // In production, send this to an analytics service
    // Example: sendToAnalytics({ name, duration, metadata });
  }

  /**
   * Log navigation metrics
   */
  private logNavigationMetrics(entry: PerformanceNavigationTiming): void {
    const metrics = {
      'DNS Lookup': entry.domainLookupEnd - entry.domainLookupStart,
      'TCP Connection': entry.connectEnd - entry.connectStart,
      'TLS Handshake': entry.secureConnectionStart > 0 ? entry.connectEnd - entry.secureConnectionStart : 0,
      'Request': entry.responseStart - entry.requestStart,
      'Response': entry.responseEnd - entry.responseStart,
      'DOM Processing': entry.domComplete - entry.domContentLoadedEventStart,
      'Load Complete': entry.loadEventEnd - entry.loadEventStart,
      'Total Load Time': entry.loadEventEnd - entry.fetchStart
    };

    Object.entries(metrics).forEach(([name, duration]) => {
      if (duration > 0) {
        this.logMetric(`Navigation: ${name}`, 0, duration);
      }
    });
  }

  /**
   * Log resource metrics
   */
  private logResourceMetrics(entry: PerformanceResourceTiming): void {
    const resourceType = this.getResourceType(entry.name);

    this.logMetric(`Resource: ${resourceType}`, entry.startTime, entry.responseEnd, {
      url: entry.name,
      size: entry.transferSize,
      cached: entry.transferSize === 0 && entry.decodedBodySize > 0
    });
  }

  /**
   * Get resource type from URL
   */
  private getResourceType(url: string): string {
    if (url.includes('.css')) return 'CSS';
    if (url.includes('.js')) return 'JavaScript';
    if (url.match(/\.(jpg|jpeg|png|gif|webp|svg)$/i)) return 'Image';
    if (url.match(/\.(woff|woff2|ttf|eot)$/i)) return 'Font';
    return 'Other';
  }

  /**
   * Get all metrics
   */
  getMetrics(): PerformanceMetrics[] {
    return Array.from(this.metrics.values());
  }

  /**
   * Clear all metrics
   */
  clearMetrics(): void {
    this.metrics.clear();
  }

  /**
   * Check if performance monitoring is initialized
   */
  isInitialized(): boolean {
    return this.initialized;
  }

  /**
   * Check if performance monitoring is currently initializing
   */
  isInitializing(): boolean {
    return this.initializing;
  }

  /**
   * Get number of active observers
   */
  getObserverCount(): number {
    return this.observers?.length || 0;
  }

  /**
   * Internal cleanup method for observers only
   */
  private cleanupObservers(): void {
    if (this.observers?.length > 0) {
      this.observers.forEach(observer => observer.disconnect());
      this.observers = [];
    }
  }

  /**
   * Public cleanup method for complete reset
   */
  cleanup(): void {
    this.cleanupObservers();
    this.initialized = false;
    this.initializing = false;
  }
}

/**
 * Measure component render time
 */
export function measureComponentRender<T extends Record<string, unknown>>(
  Component: React.ComponentType<T>,
  componentName?: string
): React.ComponentType<T> {
  const name = componentName || Component.displayName || Component.name || 'Component';

  return function MeasuredComponent(props: T) {
    React.useEffect(() => {
      // Lazy access to monitor to avoid race condition
      const monitor = PerformanceMonitor.getInstance();
      monitor.startMeasure(`${name} Mount`);
      return () => {
        monitor.endMeasure(`${name} Mount`);
      };
    }, []);

    React.useEffect(() => {
      // Lazy access to monitor to avoid race condition
      const monitor = PerformanceMonitor.getInstance();
      monitor.startMeasure(`${name} Render`);
      monitor.endMeasure(`${name} Render`);
    });

    return React.createElement(Component, props);
  };
}

/**
 * Hook for measuring custom performance metrics
 */
export function usePerformanceMetric(name: string, dependencies: React.DependencyList = []) {
  // Use lazy initialization to avoid race condition
  const getMonitor = React.useCallback(() => PerformanceMonitor.getInstance(), []);

  React.useEffect(() => {
    const monitor = getMonitor();
    monitor.startMeasure(name);
    return () => {
      monitor.endMeasure(name);
    };
  }, [getMonitor, name, ...dependencies]);

  return React.useMemo(() => ({
    startMeasure: (metricName: string) => getMonitor().startMeasure(metricName),
    endMeasure: (metricName: string) => getMonitor().endMeasure(metricName)
  }), [getMonitor]);
}

/**
 * Initialize performance monitoring (should only be called once from main.tsx)
 */
export function initializePerformanceMonitoring(): PerformanceMonitor {
  const monitor = PerformanceMonitor.getInstance();
  monitor.initialize();
  return monitor;
}

/**
 * Get the performance monitor instance (only after initialization)
 */
export function getPerformanceMonitor(): PerformanceMonitor {
  return PerformanceMonitor.getInstance();
}

